import { memo, useState } from "react";
import styles from "@styles";

export default memo(function ChatMessage({ author, newUser, children }) {
    const [badges, setBadges] = useState([
        "data:image/jpeg;base64,", "data:image/jpeg;base64,","data:image/jpeg;base64,", "data:image/jpeg;base64,", "data:image/jpeg;base64,", "data:image/jpeg;base64,", "data:image/jpeg;base64,", "data:image/jpeg;base64,", "data:image/jpeg;base64,", "data:image/jpeg;base64,"
    ]);

    return (
        // <li className={styles.chat.message} style={{ marginTop: newUser ? "15px" : "", paddingLeft: newUser ? "10px" : "70px" }}>
        <li className={styles.chat.message} style={{ marginTop: newUser ? "15px" : "" }}>
            {newUser && <div>
                <div className={`${styles.chat.messageAvatarContainer} ${styles.chat.messageAvatar}`}>
                    <img className={styles.chat.avatar} src={author.avatar === null ? "/content/blooks/Default.png" : author.avatar} />
                </div>
            </div>}
            <div>
                <div className={styles.chat.usernameContainer}>
                    {newUser && <><div className={`
                        ${styles.chat.messageUsername}
                        ${author.color === "rainbow" ? styles.textFormatting.rainbow : ""}
                    `} style={{ color: author.color }}>{author.username}
                    </div>

                    <div className={styles.chat.messageBadgeContainer}>{

                        badges.map((badge, index) => {
                            return <img key={index} src={badge} className={styles.chat.messageBadge} />
                        })

                    }</div></>}
                </div>
                <div className={styles.chat.messageContent}>{children}</div>
            </div>
        </li>
    )
});